import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../../css/AssignmentPerformancePage.css'; // Import the CSS file

const AssignmentPerformancePage = () => {
  const { assignmentId } = useParams();
  const [performanceData, setPerformanceData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // In a real application, you would fetch performance data for the assignmentId here
  useEffect(() => {
    // Placeholder for fetching data
    console.log(`Fetching performance data for assignment: ${assignmentId}`);
    // Example fetch:
    // const fetchPerformance = async () => {
    //   try {
    //     const response = await fetch(`/api/instructor/assignments/${assignmentId}/performance`);
    //     if (!response.ok) {
    //       throw new Error(`HTTP error! status: ${response.status}`);
    //     }
    //     const data = await response.json();
    //     setPerformanceData(data);
    //   } catch (error) {
    //     setError(error);
    //   } finally {
    //     setLoading(false);
    //   }
    // };
    // fetchPerformance();

    // Simulating data loading
    const timer = setTimeout(() => {
      setPerformanceData({ // Sample data based on Figma
        assignmentTitle: `Assignment ${assignmentId}`,
        submittedOnTime: '85%',
        submittedAfterDeadline: '15%',
        // Add data for pie chart if needed
      });
      setLoading(false);
    }, 1000);

    return () => clearTimeout(timer);

  }, [assignmentId]);

  if (loading) {
    return <div>Loading Assignment Performance...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  if (!performanceData) {
    return <div>Performance data not found for assignment {assignmentId}</div>;
  }

  return (
    <div className="assignment-performance-container">
      <h1>Assignment Performance for {performanceData.assignmentTitle}</h1>

      <div className="performance-summary">
        <p>Submitted Assignments on time: {performanceData.submittedOnTime}</p>
        <p>Submitted Assignments after deadline: {performanceData.submittedAfterDeadline}</p>
        {/* Placeholder for Pie Chart */}
        <div className="pie-chart-placeholder">Pie Chart</div>
      </div>

      {/* Placeholder for CRUD operations related to the assignment itself or performance data */}
      <div className="crud-section">
        <h2>Manage Assignment</h2>
        {/* Forms or links for Edit/Delete Assignment, Add/Edit/Delete performance data if applicable */}
        <p>CRUD operation interfaces will go here.</p>
      </div>
    </div>
  );
};

export default AssignmentPerformancePage;
