import React, { useState } from 'react';
import axios from 'axios';
import './InstructorNotificationForm.js';

const InstructorNotificationForm = () => {
  const [message, setMessage] = useState('');
  const [feedback, setFeedback] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.post('http://localhost:5000/api/notifications', { message });
      setFeedback('Notification sent successfully!');
      setMessage('');
    } catch (err) {
      console.error('Error sending notification:', err);
      setFeedback('Failed to send notification.');
    }
  };

  return (
    <div className="notification-page">
      <h2>Send Notification</h2>
      <form onSubmit={handleSubmit}>
        <textarea
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Enter your notification message..."
          required
        />
        <button type="submit">Send</button>
      </form>
      {feedback && <p className="feedback">{feedback}</p>}
    </div>
  );
};

export default InstructorNotificationForm;
