import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MarksShowing.css';

const MarksShowing = () => {
  const [marks, setMarks] = useState({
    mid: 0,
    quiz: 0,
    assignment: 0,
  });

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/student/marks/BSC0123');
        setMarks(res.data);
      } catch (err) {
        console.error('Failed to fetch marks:', err);
      }
    };

    fetchMarks();
  }, []);

  const getLevel = (score) => {
    if (score >= 70) return 'Excellent';
    if (score >= 50) return 'Very Good';
    return 'Good';
  };

  const renderMarkCard = (label, value) => (
    <div className="mark-card">
      <h3>{label}</h3>
      <div className="mark-score">
        <span>{value}/100</span>
      </div>
      <div className="mark-levels">
        <p className={getLevel(value) === 'Excellent' ? 'active' : ''}>Excellent {getLevel(value) === 'Excellent' && '⭐'}</p>
        <p className={getLevel(value) === 'Very Good' ? 'active' : ''}>Very Good {getLevel(value) === 'Very Good' && '⭐'}</p>
        <p className={getLevel(value) === 'Good' ? 'active' : ''}>Good {getLevel(value) === 'Good' && '⭐'}</p>
      </div>
    </div>
  );

  return (
    <div className="marks-page">
      <h1>Marks</h1>
      <div className="marks-container">
        {renderMarkCard('Mid Marks', marks.mid)}
        {renderMarkCard('Quiz Marks', marks.quiz)}
        {renderMarkCard('Assignment Marks', marks.assignment)}
      </div>
    </div>
  );
};

export default MarksShowing;
