import React, { useState, useEffect, useRef } from "react";

import axios from "axios";
import "./StudentPrivateFiles.css";

const StudentPrivateFiles = () => {
  const [file, setFile] = useState(null);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const fileInputRef = useRef();

  const studentName = "John Doe"; // Replace with dynamic student name if available

  const fetchFiles = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/student/files/${studentName}`);
      setUploadedFiles(res.data);
    } catch (err) {
      console.error("Error fetching files", err);
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);
    formData.append("studentName", studentName);

    try {
      await axios.post("http://localhost:5000/api/student/files/upload", formData);
      setFile(null);
      fileInputRef.current.value = null;
      fetchFiles(); // Refresh list
    } catch (err) {
      console.error("Upload failed", err);
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Upload Private File</h2>
      <input type="file" onChange={handleFileChange} ref={fileInputRef} />
      <button onClick={handleUpload} disabled={!file}>
        Upload
      </button>

      <h3>Uploaded Files</h3>
      {uploadedFiles.length === 0 ? (
        <p>No files uploaded yet.</p>
      ) : (
        <ul>
          {uploadedFiles.map((fileData) => (
            <li key={fileData._id}>
              <a href={`http://localhost:5000/${fileData.filePath}`} target="_blank" rel="noreferrer">
                {fileData.filePath.split("/").pop()}
              </a> — Uploaded at: {new Date(fileData.uploadedAt).toLocaleString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StudentPrivateFiles;
