import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import './StudentNotificationPage.css';

const StudentNotificationPage = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchNotifications = async () => {
    try {
      const { data } = await axios.get('/api/student/notifications');
      setNotifications(data.notifications || data);
      setLoading(false);
    } catch (error) {
      toast.error('Failed to load notifications');
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotifications();
    
    // Set up polling every 30 seconds
    const interval = setInterval(fetchNotifications, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return <div className="loading">Loading notifications...</div>;
  }

  return (
    <div className="notifications-container">
      <h2>Notifications</h2>
      
      {notifications.length === 0 ? (
        <div className="empty-state">
          No notifications available
        </div>
      ) : (
        <div className="notifications-list">
          {notifications.map(notification => (
            <div 
              key={notification._id} 
              className={`notification-card ${notification.isImportant ? 'important' : ''}`}
            >
              <div className="notification-header">
                <h3>{notification.title}</h3>
                <span className="notification-date">
                  {new Date(notification.createdAt).toLocaleString()}
                </span>
              </div>
              
              {notification.course && (
                <div className="notification-course">
                  {notification.course.name} ({notification.course.code})
                </div>
              )}
              
              <div className="notification-message">
                {notification.message}
              </div>
              
              <div className="notification-footer">
                <span className="notification-instructor">
                  From: {notification.instructor.name}
                </span>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default StudentNotificationPage;
