import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css';

const StudentPrivateFiles = () => {
  const studentId = 'BSC0123';
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  const fetchFiles = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/student/files/${studentId}`);
      setFiles(res.data);
    } catch (err) {
      console.error('Error fetching files:', err);
    }
  };

  useEffect(() => {
    fetchFiles();
  }, []);

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    setSelectedFile(droppedFile);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('studentId', studentId);

    try {
      setUploading(true);
      await axios.post('http://localhost:5000/api/student/upload-file', formData);
      setSelectedFile(null);
      fetchFiles();
    } catch (err) {
      console.error('Upload failed:', err);
    } finally {
      setUploading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/student/file/${id}`);
      fetchFiles();
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  return (
    <div className="private-files-page">
      <h2>My Private Files</h2>

      <div
        className="drop-zone"
        onDrop={handleDrop}
        onDragOver={(e) => e.preventDefault()}
      >
        <p>Drag & drop file here or click below</p>
        <input
          type="file"
          onChange={(e) => setSelectedFile(e.target.files[0])}
        />
        {selectedFile && (
          <div className="upload-box">
            <p>{selectedFile.name}</p>
            <button onClick={handleUpload} disabled={uploading}>
              {uploading ? 'Uploading...' : 'Upload'}
            </button>
          </div>
        )}
      </div>

      <div className="file-list">
        {files.length === 0 ? (
          <p>No files uploaded yet.</p>
        ) : (
          files.map((file) => (
            <div className="file-card" key={file._id}>
              <p>{file.filename}</p>
              <div className="file-actions">
                <a
                  href={`http://localhost:5000/${file.filepath}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  View
                </a>
                <button onClick={() => handleDelete(file._id)}>Delete</button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default StudentPrivateFiles;
