import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../css/InstructorModuleDisplayPage.css'; // Import the CSS file

const InstructorModuleDisplayPage = () => {
  const { moduleId } = useParams();
  const [module, setModule] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchModuleDetails = async () => {
      try {
        const response = await fetch(`/api/instructor/modules/${moduleId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setModule(data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };

    fetchModuleDetails();
  }, [moduleId]);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  if (!module) {
    return <div>Module not found</div>;
  }

  return (
    <div className="instructor-module-container">
      <h1>{module.title}</h1>
      <p>{module.description}</p>

      <div className="module-section">
        <h3>Lecture Materials</h3>
        {/* Display existing lecture materials and form to add new */}
      </div>

      <div className="module-section">
        <h3>Quizzes</h3>
        {/* Display existing quizzes and form to add new */}
      </div>

      <div className="module-section">
        <h3>Assignments</h3>
        {/* Display existing assignments and form to add new */}
      </div>

      <div className="module-section">
        <h3>Announcements</h3>
        {/* Display existing announcements and form to add new */}
      </div>
    </div>
  );
};

export default InstructorModuleDisplayPage;
