import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AssignmentSubmission.css";

const AssignmentSubmission = () => {
  const openDate = new Date("2025-04-05T00:00:00");
  const dueDate = new Date("2025-05-12T00:00:00");

  const [file, setFile] = useState(null);
  const [submission, setSubmission] = useState(null);
  const [loading, setLoading] = useState(true);
  const studentName = "John Doe"; // Replace with dynamic user if available

  const backendURL = "http://localhost:5000/api/student/assignment";

  useEffect(() => {
    const fetchSubmission = async () => {
      try {
        const res = await axios.get(`${backendURL}/${studentName}`);
        if (res.data && res.data._id) {
          setSubmission(res.data);
        }
      } catch (err) {
        console.error("Error fetching submission", err);
      } finally {
        setLoading(false);
      }
    };
    fetchSubmission();
  }, []);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);
    formData.append("studentName", studentName);
    formData.append("title", "Assignment 01");

    try {
      const res = await axios.post(`${backendURL}/submit`, formData);
      setSubmission(res.data);
    } catch (err) {
      alert("Submission failed: " + err.response?.data?.error || err.message);
    }
  };

  const handleEdit = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.put(`${backendURL}/edit/${studentName}`, formData);
      setSubmission(res.data);
    } catch (err) {
      alert("Edit failed: " + err.response?.data?.error || err.message);
    }
  };

  const getTimeRemainingText = () => {
    const now = submission?.submittedAt ? new Date(submission.submittedAt) : new Date();
    const diffMs = dueDate - now;
    const absDiff = Math.abs(diffMs);
    const diffDays = Math.floor(absDiff / (1000 * 60 * 60 * 24));
    const diffHours = Math.floor((absDiff / (1000 * 60 * 60)) % 24);

    if (!submission?.submittedAt) {
      return `${diffMs >= 0 ? "" : "Overdue by "}${diffDays} days and ${diffHours} hours`;
    }

    if (now < dueDate) {
      return `Submitted ${diffDays} days and ${diffHours} hours early`;
    } else {
      return `Overdue by ${diffDays} days and ${diffHours} hours`;
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="assignment-page">
      <div className="assignment-container">
        <h4 className="assignment-title">Principles of Management</h4>

        <div>
          <h5 className="assignment-subheading">Assignment 01</h5>
          <p className="assignment-dates">Opened: {openDate.toLocaleString()}</p>
          <p className="assignment-dates">Due: {dueDate.toLocaleString()}</p>

          {!submission && (
            <button className="mark-as-done-btn">Mark as done</button>
          )}

          <table className="status-table">
            <thead>
              <tr>
                <th colSpan="2">Submission Status</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Submission Status</td>
                <td>{submission ? "Submitted" : "No attempt"}</td>
              </tr>
              <tr>
                <td>Grading Status</td>
                <td>{submission ? "Not Graded" : "-"}</td>
              </tr>
              <tr>
                <td>Time Remaining</td>
                <td>{getTimeRemainingText()}</td>
              </tr>
              <tr>
                <td>Last Modified</td>
                <td>{submission ? new Date(submission.submittedAt).toLocaleString() : "-"}</td>
              </tr>
              <tr>
                <td>Submission Comments</td>
                <td>{submission ? "Submitted successfully." : "No comments"}</td>
              </tr>
            </tbody>
          </table>

          <div className="submission-actions">
            {!submission ? (
              <>
                <input
                  type="file"
                  onChange={handleFileChange}
                  className="file-input"
                  accept=".pdf,.doc,.docx"
                />
                <button className="submit-btn" onClick={handleSubmit} disabled={!file}>
                  Submit Assignment
                </button>
                <p>You have not made a submission yet</p>
              </>
            ) : (
              <>
                <p className="uploaded-file">Uploaded: {submission.filePath.split("/").pop()}</p>
                <input
                  type="file"
                  onChange={handleFileChange}
                  className="file-input"
                  accept=".pdf,.doc,.docx"
                />
                <button className="edit-btn" onClick={handleEdit} disabled={!file}>
                  Edit Submission
                </button>
              </>
            )}
            <button className="back-btn">Back To Course</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssignmentSubmission;
