import React, { useEffect, useState } from 'react';
import './MarksShowing.css';
import { FaStar } from 'react-icons/fa';
import axios from 'axios';

const MarksShowing = () => {
  const [marks, setMarks] = useState(null);

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/student/marks');
        setMarks(res.data);
      } catch (error) {
        console.error('Failed to fetch marks:', error);
      }
    };

    fetchMarks();
  }, []);

  const getLevel = (score) => {
    if (score >= 75) return 'Excellent';
    if (score >= 60) return 'Very Good';
    return 'Good';
  };

  const renderStar = (level, actualLevel) => {
    return level === actualLevel ? <FaStar className="star" /> : null;
  };

  if (!marks) {
    return <div className="marks-loading">Loading...</div>; // Only briefly shown until API returns
  }

  const markItems = [
    { title: 'Mid Marks', score: marks.midMarks },
    { title: 'Quiz Marks', score: marks.quizMarks },
    { title: 'Assignment Marks', score: marks.assignmentMarks },
  ];

  return (
    <div className="marks-page">
      <h1 className="marks-title">Marks</h1>
      <div className="marks-container">
        {markItems.map(({ title, score }) => {
          const level = getLevel(score);
          return (
            <div className="marks-box" key={title}>
              <h2>{title}</h2>
              <div className="marks-score">{score}/100</div>
              <div className="marks-levels">
                <div>Excellent {renderStar('Excellent', level)}</div>
                <div>Very Good {renderStar('Very Good', level)}</div>
                <div>Good {renderStar('Good', level)}</div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default MarksShowing;
