import React from 'react';
import { Route, Routes } from "react-router-dom";
import SemesterDisplay from "./pages/instructor/SemesterDisplay";
import ModuleDisplay from "./pages/instructor/ModuleDisplay";
import DiplomaYearDisplay from "./pages/instructor/DiplomaYearDisplay";
import 'bootstrap/dist/css/bootstrap.min.css';
import "bootstrap/dist/js/bootstrap.bundle.min.js";

function App() {
  // Get the current path to determine which page to show
  const path = window.location.pathname;

  // Render the appropriate component based on the path
  let component;
  if (path === "/modules") {
    component = <ModuleDisplay />;
  } else if (path === "/diploma-years") {
    component = <DiplomaYearDisplay />;
  } else {
    component = <SemesterDisplay />;
  }

  return (
    <div>
      {component}
    </div>
  );
}

export default App;
