// src/pages/student/AssignmentSubmission.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AssignmentSubmission.css';

function AssignmentSubmission() {
  const [assignment, setAssignment] = useState(null);
  const [file, setFile] = useState(null);
  const [previewFileName, setPreviewFileName] = useState('');
  const [message, setMessage] = useState('');
  const [submissionStatus, setSubmissionStatus] = useState(null);

  useEffect(() => {
    // Fetch assignment info and student submission status
    axios.get('http://localhost:5000/api/student/assignment')
      .then((res) => {
        setAssignment(res.data.assignment);
        setSubmissionStatus(res.data.submission);
      })
      .catch((err) => console.error(err));
  }, []);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    setFile(selectedFile);
    setPreviewFileName(selectedFile.name);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    setFile(droppedFile);
    setPreviewFileName(droppedFile.name);
  };

  const handleDragOver = (e) => e.preventDefault();

  const handleSubmit = async () => {
    if (!file) {
      setMessage('Please select or drag a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('assignmentFile', file);

    try {
      const response = await axios.post('http://localhost:5000/api/student/assignment', formData);
      setMessage(response.data.message);
      setSubmissionStatus(response.data.submission);
      setFile(null);
      setPreviewFileName('');
    } catch (error) {
      console.error(error);
      setMessage('Submission failed. Try again.');
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete('http://localhost:5000/api/student/assignment');
      setMessage('Submission deleted.');
      setSubmissionStatus(null);
      setFile(null);
      setPreviewFileName('');
    } catch (err) {
      console.error(err);
      setMessage('Error deleting submission.');
    }
  };

  const formatTimeRemaining = () => {
    if (!assignment?.dueDate) return '';
    const due = new Date(assignment.dueDate);
    const now = new Date();
    const diff = due - now;

    if (diff <= 0) return 'Overdue';
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff / (1000 * 60 * 60)) % 24);
    const minutes = Math.floor((diff / (1000 * 60)) % 60);
    return `${days}d ${hours}h ${minutes}m`;
  };

  return (
    <div className="assignment-container">
      <h3 className="course-title">Principles of Management</h3>

      <div className="assignment-header">
        <h4>Assignment 01</h4>
        <p><strong>Opened:</strong> {assignment?.openDate ? new Date(assignment.openDate).toLocaleString() : 'N/A'}</p>
        <p><strong>Due:</strong> {assignment?.dueDate ? new Date(assignment.dueDate).toLocaleString() : 'N/A'}</p>
        <button className="mark-done-btn">Mark as Done</button>
      </div>

      <div className="assignment-status-box">
        <div className="status-row">
          <span>Submission Status:</span>
          <span>{submissionStatus ? 'Submitted' : 'No attempt'}</span>
        </div>
        <div className="status-row">
          <span>Time Remaining:</span>
          <span>{formatTimeRemaining()}</span>
        </div>
        <div className="status-row">
          <span>Last Modified:</span>
          <span>
            {submissionStatus?.submittedAt ? new Date(submissionStatus.submittedAt).toLocaleString() : '-'}
          </span>
        </div>
      </div>

      <div 
        className="dropzone"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <p>Drag and drop your file here, or</p>
        <label className="file-select-label">
          Click to choose a file
          <input type="file" onChange={handleFileChange} />
        </label>
        {previewFileName && <p className="filename-preview">Selected: {previewFileName}</p>}
      </div>

      <div className="button-group">
        <button onClick={handleSubmit} className="submit-btn">
          {submissionStatus ? 'Edit Submission' : 'Add Submission'}
        </button>
        {submissionStatus && (
          <button onClick={handleDelete} className="delete-btn">Delete Submission</button>
        )}
      </div>

      {message && <p className="message">{message}</p>}

      <button className="back-btn">Back To Course</button>
    </div>
  );
}

export default AssignmentSubmission;
