import React, { useState } from "react";
import axios from "axios";
import "./AssignmentSubmission.css";

const AssignmentSubmission = () => {
  const openDate = new Date("2025-04-05T00:00:00");
  const dueDate = new Date("2025-05-12T00:00:00");

  const [file, setFile] = useState(null);
  const [submittedAt, setSubmittedAt] = useState(null);
  const [uploadSuccess, setUploadSuccess] = useState(false);
  const [uploadedFileName, setUploadedFileName] = useState("");

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setUploadSuccess(false);
  };

  const handleSubmit = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append("assignmentFile", file);
    formData.append("studentId", "660123abc456def789012345"); // Replace with actual student ID
    formData.append("assignmentTitle", "Assignment 01");

    try {
      const response = await axios.post(
        "http://localhost:5000/api/student/submit-assignment",
        formData
      );
      console.log("Upload response:", response.data);

      setSubmittedAt(new Date());
      setUploadSuccess(true);
      setUploadedFileName(file.name);
    } catch (error) {
      console.error("Upload failed:", error);
    }
  };

  const handleEdit = () => {
    setFile(null);
    setSubmittedAt(null);
    setUploadSuccess(false);
    setUploadedFileName("");
  };

  const getTimeRemainingText = () => {
    if (!submittedAt) {
      const now = new Date();
      const diff = dueDate - now;
      const isOverdue = diff < 0;
      const absDiff = Math.abs(diff);
      const days = Math.floor(absDiff / (1000 * 60 * 60 * 24));
      const hours = Math.floor((absDiff / (1000 * 60 * 60)) % 24);

      return `${isOverdue ? "Overdue by" : ""} ${days} days and ${hours} hours`;
    }

    const diff = dueDate - submittedAt;
    const absDiff = Math.abs(diff);
    const days = Math.floor(absDiff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((absDiff / (1000 * 60 * 60)) % 24);

    if (submittedAt < dueDate) {
      return `Submitted ${days} days and ${hours} hours early`;
    } else {
      return `Overdue by ${days} days and ${hours} hours`;
    }
  };

  return (
    <div className="assignment-page">
      <div className="assignment-container">
        <h4 className="assignment-title">Principles of Management</h4>

        <div>
          <h5 className="assignment-subheading">Assignment 01</h5>
          <p className="assignment-dates">
            Opened: {openDate.toLocaleString()}
          </p>
          <p className="assignment-dates">
            Due: {dueDate.toLocaleString()}
          </p>

          {!submittedAt && (
            <button className="mark-as-done-btn">Mark as done</button>
          )}

          <table className="status-table">
            <thead>
              <tr>
                <th colSpan="2">Submission Status</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Submission Status</td>
                <td>{submittedAt ? "Submitted" : "No attempt"}</td>
              </tr>
              <tr>
                <td>Grading Status</td>
                <td>{submittedAt ? "Not Graded" : "-"}</td>
              </tr>
              <tr>
                <td>Time Remaining</td>
                <td>{getTimeRemainingText()}</td>
              </tr>
              <tr>
                <td>Last Modified</td>
                <td>{submittedAt ? submittedAt.toLocaleString() : "-"}</td>
              </tr>
              <tr>
                <td>Submission Comments</td>
                <td>{submittedAt ? "Submitted successfully." : "No comments"}</td>
              </tr>
            </tbody>
          </table>

          <div className="submission-actions">
            {!submittedAt ? (
              <>
                <input
                  type="file"
                  onChange={handleFileChange}
                  className="file-input"
                  accept=".pdf,.doc,.docx"
                />
                <button
                  className="submit-btn"
                  onClick={handleSubmit}
                  disabled={!file}
                >
                  Submit Assignment
                </button>
                <p>You have not made a submission yet</p>
              </>
            ) : (
              <>
                <p className="uploaded-file">Uploaded: {uploadedFileName}</p>
                <button className="edit-btn" onClick={handleEdit}>
                  Edit Submission
                </button>
              </>
            )}

            {uploadSuccess && (
              <p className="success-message">Assignment submitted successfully!</p>
            )}

            <button className="back-btn">Back To Course</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssignmentSubmission;
