// File: frontend/src/pages/student/MarksShowing.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import "./marksShowing.css";

const MarksShowing = () => {
  const [marks, setMarks] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:5000/api/student/marks") // Update if using different port or proxy
      .then((res) => setMarks(res.data))
      .catch((err) => console.error("Failed to fetch marks", err));
  }, []);

  return (
    <div className="marks-page">
      <h2>Your Marks</h2>
      <div className="marks-section">
        {marks.length === 0 ? (
          <p>No marks available.</p>
        ) : (
          marks.map((item, index) => (
            <div className="mark-card" key={index}>
              <h3>{item.subject}</h3>
              <p><strong>Type:</strong> {item.type}</p>
              <p><strong>Marks:</strong> {item.score} / {item.total}</p>
              <button onClick={() => alert(`Details for ${item.subject} (${item.type})`)}>View</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default MarksShowing;
