import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MarksShowing.css';

const MarksShowing = () => {
  const [marks, setMarks] = useState(null);
  const studentId = 'BSCS0123'; // Replace with dynamic ID from auth/session if needed

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/student/marks/${studentId}`);
        setMarks(response.data);
      } catch (err) {
        console.error('Failed to load marks:', err);
      }
    };
    fetchMarks();
  }, []);

  const getLevel = (score) => {
    if (score >= 70) return 'Excellent';
    if (score >= 50) return 'Very Good';
    return 'Good';
  };

  return (
    <div className="marks-page">
      <h1 className="marks-title">Marks</h1>
      {marks ? (
        <div className="marks-container">
          {[
            { label: 'Mid Marks', score: marks.mid },
            { label: 'Quiz Marks', score: marks.quiz },
            { label: 'Assignment Marks', score: marks.assignment },
          ].map((item, index) => (
            <div className="marks-card" key={index}>
              <h2>{item.label}</h2>
              <div className="marks-score">
                <span>{item.score}/100</span>
              </div>
              <ul className="marks-levels">
                {['Excellent', 'Very Good', 'Good'].map((level) => (
                  <li key={level}>
                    {level}
                    {getLevel(item.score) === level && <span className="star">â˜…</span>}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      ) : (
        <p className="loading-text">Loading marks...</p>
      )}
    </div>
  );
};

export default MarksShowing;
