// src/pages/student/AssignmentSubmission.js

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AssignmentSubmission.css';

function AssignmentSubmission() {
  const [assignment, setAssignment] = useState({});
  const [file, setFile] = useState(null);
  const [previewFileName, setPreviewFileName] = useState('');
  const [submission, setSubmission] = useState(null);
  const [message, setMessage] = useState('');

  // Fetch assignment & submission status
  useEffect(() => {
    axios.get('http://localhost:5000/api/student/assignment')
      .then(res => {
        setAssignment(res.data.assignment);
        setSubmission(res.data.submission);
      })
      .catch(err => console.error('Fetch error:', err));
  }, []);

  const handleFileChange = (e) => {
    const selected = e.target.files[0];
    setFile(selected);
    setPreviewFileName(selected?.name);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const dropped = e.dataTransfer.files[0];
    setFile(dropped);
    setPreviewFileName(dropped?.name);
  };

  const handleDragOver = (e) => e.preventDefault();

  const handleSubmit = async () => {
    if (!file) {
      setMessage('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('assignmentFile', file);

    try {
      const res = await axios.post('http://localhost:5000/api/student/assignment', formData);
      setMessage('Submission successful!');
      setSubmission(res.data.submission);
      setFile(null);
      setPreviewFileName('');
    } catch (err) {
      console.error(err);
      setMessage('Submission failed. Try again.');
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete('http://localhost:5000/api/student/assignment');
      setSubmission(null);
      setMessage('Submission deleted.');
    } catch (err) {
      console.error(err);
      setMessage('Delete failed.');
    }
  };

  const timeRemaining = () => {
    if (!assignment.dueDate) return '';
    const now = new Date();
    const due = new Date(assignment.dueDate);
    const diff = due - now;

    if (diff <= 0) return 'Overdue';

    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hrs = Math.floor((diff / (1000 * 60 * 60)) % 24);
    const mins = Math.floor((diff / (1000 * 60)) % 60);

    return `${days}d ${hrs}h ${mins}m`;
  };

  return (
    <div className="assignment-container">
      <h2>Assignment Submission</h2>
      <div className="assignment-dates">
        <p><strong>Opened:</strong> {assignment.openDate ? new Date(assignment.openDate).toLocaleString() : 'Loading...'}</p>
        <p><strong>Due:</strong> {assignment.dueDate ? new Date(assignment.dueDate).toLocaleString() : 'Loading...'}</p>
        <p><strong>Time Remaining:</strong> {timeRemaining()}</p>
      </div>

      <div className="submission-box">
        <p><strong>Status:</strong> {submission ? 'Submitted' : 'Not submitted'}</p>
        <p><strong>Last Modified:</strong> {submission?.submittedAt ? new Date(submission.submittedAt).toLocaleString() : '-'}</p>
      </div>

      <div 
        className="dropzone"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <p>Drag and drop your file here, or</p>
        <label className="file-select-label">
          Choose file
          <input type="file" onChange={handleFileChange} />
        </label>
        {previewFileName && <p className="filename-preview">Selected: {previewFileName}</p>}
      </div>

      <div className="button-group">
        <button onClick={handleSubmit}>
          {submission ? 'Edit Submission' : 'Add Submission'}
        </button>
        {submission && (
          <button className="delete-btn" onClick={handleDelete}>Delete Submission</button>
        )}
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default AssignmentSubmission;
