import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MarksShowing.css';

const MarksShowing = () => {
  const [marks, setMarks] = useState(null);

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/student/marks/BSC0123');
        setMarks(response.data);
      } catch (error) {
        console.error('Error fetching marks:', error);
      }
    };

    fetchMarks();
  }, []);

  const getLevel = (score) => {
    if (score >= 70) return 'Excellent';
    if (score >= 50) return 'Very Good';
    return 'Good';
  };

  return (
    <div className="marks-page">
      <h1 className="marks-heading">Marks</h1>
      {marks ? (
        <div className="marks-container">
          {['mid', 'quiz', 'assignment'].map((type) => (
            <div className="marks-card" key={type}>
              <h2>{type.charAt(0).toUpperCase() + type.slice(1)} Marks</h2>
              <div className="score">
                <span>{marks[type]} / 100</span>
              </div>
              <ul className="levels">
                {['Excellent', 'Very Good', 'Good'].map((level) => (
                  <li key={level}>
                    {level}
                    {getLevel(marks[type]) === level && <span className="star">â˜…</span>}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      ) : (
        <p>Loading marks...</p>
      )}
    </div>
  );
};

export default MarksShowing;
