import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css'; // Optional CSS

const StudentPrivateFiles = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [studentId] = useState('student123'); // Replace this with real user ID from auth
  const [message, setMessage] = useState('');
  const [privateFiles, setPrivateFiles] = useState([]);

  useEffect(() => {
    fetchPrivateFiles();
  }, []);

  const fetchPrivateFiles = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/student/private-files/${studentId}`);
      setPrivateFiles(res.data);
    } catch (err) {
      console.error('Fetch error:', err);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setMessage('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('studentId', studentId);

    try {
      await axios.post('http://localhost:5000/api/student/private-files/upload', formData);
      setMessage('Upload successful!');
      setSelectedFile(null);
      fetchPrivateFiles();
    } catch (err) {
      console.error('Upload error:', err);
      setMessage('Upload failed. Try again.');
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/student/private-files/${id}`);
      fetchPrivateFiles();
    } catch (err) {
      console.error('Delete error:', err);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Upload Your Private Files</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      {message && <p>{message}</p>}

      <h3>Uploaded Files</h3>
      <ul>
        {privateFiles.map(file => (
          <li key={file._id}>
            <a
              href={`http://localhost:5000/${file.filePath}`}
              target="_blank"
              rel="noopener noreferrer"
            >
              {file.originalName}
            </a>
            <button onClick={() => handleDelete(file._id)} style={{ marginLeft: '10px' }}>
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default StudentPrivateFiles;
