import React, { useState } from "react";
import "./AssignmentSubmission.css";

const AssignmentSubmission = () => {
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);

  const handleFileChange = (e) => {
    setUploadedFile(e.target.files[0]);
  };

  const handleSubmit = () => {
    if (uploadedFile) {
      setIsSubmitted(true);
    } else {
      alert("Please upload a file before submitting.");
    }
  };

  return (
    <div className="assignment-submission-container">
      <h2>Assignment Submission</h2>

      <div className="upload-section">
        <label htmlFor="fileUpload">Upload your assignment:</label>
        <input
          type="file"
          id="fileUpload"
          accept=".pdf,.doc,.docx"
          onChange={handleFileChange}
        />
        <button
          className={`mark-as-done-btn ${isSubmitted ? "done" : ""}`}
          onClick={handleSubmit}
        >
          {isSubmitted ? "✅ Submitted" : "Mark as Done"}
        </button>
      </div>

      <table className="status-table">
        <thead>
          <tr>
            <th colSpan="2">Submission Status</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Submission Status</td>
            <td>{isSubmitted ? "Submitted" : "No attempt"}</td>
          </tr>
          <tr>
            <td>Grading Status</td>
            <td>{isSubmitted ? "Waiting for Grading" : "Not Graded"}</td>
          </tr>
          <tr>
            <td>Time Remaining</td>
            <td>3 days and 5 hours</td>
          </tr>
          <tr>
            <td>Last Modified</td>
            <td>{isSubmitted && uploadedFile ? uploadedFile.name : "-"}</td>
          </tr>
          <tr>
            <td>Submission Comments</td>
            <td>{isSubmitted ? "Submitted Successfully." : "Comments"}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default AssignmentSubmission;
