// src/pages/student/StudentPrivateFiles.js

import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css';

const StudentPrivateFiles = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [studentId] = useState('student123'); // Hardcoded for now, can be dynamic
  const [message, setMessage] = useState('');
  const [privateFiles, setPrivateFiles] = useState([]);

  const fetchPrivateFiles = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/student/private-files/${studentId}`);
      setPrivateFiles(res.data);
    } catch (err) {
      console.error('Error fetching files:', err);
    }
  }, [studentId]);

  useEffect(() => {
    fetchPrivateFiles();
  }, [fetchPrivateFiles]);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('studentId', studentId);

    try {
      const res = await axios.post('http://localhost:5000/api/student/private-files/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage('File uploaded successfully.');
      setSelectedFile(null);
      fetchPrivateFiles(); // Refresh file list
    } catch (err) {
      console.error('Upload failed:', err);
      setMessage('Upload failed. Try again.');
    }
  };

  const handleDelete = async (fileId) => {
    try {
      await axios.delete(`http://localhost:5000/api/student/private-files/${fileId}`);
      fetchPrivateFiles(); // Refresh file list
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  return (
    <div className="student-private-files">
      <h2>Upload Private Files </h2>

      <div className="upload-section">
        <input type="file" onChange={handleFileChange} />
        <button onClick={handleUpload}>Upload</button>
      </div>

      {message && <p className="message">{message}</p>}

      <div className="uploaded-files">
        <h3>Uploaded Files</h3>
        {privateFiles.length === 0 ? (
          <p>No private files uploaded.</p>
        ) : (
          <ul>
            {privateFiles.map((file) => (
              <li key={file._id}>
                <a
                  href={`http://localhost:5000/${file.filePath}`}
                  target="_blank"
                  rel="noopener noreferrer"
                >
                  {file.originalName}
                </a>
                <button onClick={() => handleDelete(file._id)}>Delete</button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default StudentPrivateFiles;
