import React, { useEffect, useState } from 'react';
import './AssignmentSubmission.css';
import axios from 'axios';

const AssignmentSubmission = () => {
  const [assignment, setAssignment] = useState(null);
  const [file, setFile] = useState(null);
  const [comment, setComment] = useState('');
  const [submitting, setSubmitting] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchAssignment();
  }, []);

  const fetchAssignment = async () => {
    try {
      const res = await axios.get('/api/student/assignment');
      setAssignment(res.data);
    } catch (error) {
      console.error('Error fetching assignment:', error);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setFile(e.dataTransfer.files[0]);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!file) {
      alert('Please select a file');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    formData.append('comment', comment);

    try {
      setSubmitting(true);
      await axios.post('/api/student/assignment', formData);
      setMessage('Assignment submitted successfully.');
      fetchAssignment();
      setFile(null);
      setComment('');
    } catch (error) {
      console.error('Submission failed:', error);
      setMessage('Submission failed. Try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = async () => {
    if (!file) {
      alert('Please select a file to update.');
      return;
    }
    const formData = new FormData();
    formData.append('file', file);
    formData.append('comment', comment);

    try {
      await axios.put('/api/student/assignment', formData);
      setMessage('Submission updated.');
      fetchAssignment();
    } catch (error) {
      console.error('Edit failed:', error);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete('/api/student/assignment');
      setMessage('Submission deleted.');
      fetchAssignment();
    } catch (error) {
      console.error('Delete failed:', error);
    }
  };

  const calculateTimeRemaining = (dueDate) => {
    const now = new Date();
    const due = new Date(dueDate);
    const diff = due - now;
    if (diff <= 0) return 'Overdue';
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff / (1000 * 60 * 60)) % 24);
    return `${days} days and ${hours} hours`;
  };

  if (!assignment) return <div>Loading...</div>;

  return (
    <div className="assignment-container">
      <h2>Principles of Management</h2>
      <div className="assignment-header">
        <h3>Assignment 01</h3>
        <p>Opened: {new Date(assignment.openDate).toLocaleString()}</p>
        <p>Due: {new Date(assignment.dueDate).toLocaleString()}</p>
      </div>

      <div className="submission-status">
        <table>
          <tbody>
            <tr><th>Submission Status</th><td>{assignment.submitted ? 'Submitted' : 'No attempt'}</td></tr>
            <tr><th>Grading Status</th><td>{assignment.graded ? 'Graded' : 'Not Graded'}</td></tr>
            <tr><th>Time Remaining</th><td>{calculateTimeRemaining(assignment.dueDate)}</td></tr>
            <tr><th>Last Modified</th><td>{assignment.updatedAt ? new Date(assignment.updatedAt).toLocaleString() : '-'}</td></tr>
            <tr><th>Submission Comments</th><td>{assignment.comment || 'None'}</td></tr>
          </tbody>
        </table>
      </div>

      <div className="upload-section" onDragOver={(e) => e.preventDefault()} onDrop={handleDrop}>
        <p>Drag and drop your assignment file here or</p>
        <input type="file" onChange={handleFileChange} />
        <textarea
          value={comment}
          placeholder="Add submission comments..."
          onChange={(e) => setComment(e.target.value)}
        />
      </div>

      <div className="buttons">
        {!assignment.submitted ? (
          <button onClick={handleSubmit} disabled={submitting}>Add Submission</button>
        ) : (
          <>
            <button onClick={handleEdit}>Edit Submission</button>
            <button onClick={handleDelete}>Delete Submission</button>
          </>
        )}
      </div>

      {message && <p className="status-message">{message}</p>}

      <button className="back-button" onClick={() => window.history.back()}>Back To Course</button>
    </div>
  );
};

export default AssignmentSubmission;
