import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MarksShowing.css';

const MarksShowing = () => {
  const [marksData, setMarksData] = useState(null);

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/student/marks/BSC0123');
        setMarksData(res.data);
      } catch (err) {
        console.error('Failed to fetch marks:', err);
      }
    };

    fetchMarks();
  }, []);

  const getLevel = (score) => {
    if (score >= 75) return 'Excellent';
    if (score >= 50) return 'Very Good';
    return 'Good';
  };

  const renderMarkCard = ({ moduleName, assignment, quiz, mid }) => (
    <div className="mark-card">
      <h2>{moduleName}</h2>
      <div className="mark-entry">
        <h4>Assignment</h4>
        <p>{assignment}/100 <span>{getLevel(assignment)} ⭐</span></p>
      </div>
      <div className="mark-entry">
        <h4>Quiz</h4>
        <p>{quiz}/100 <span>{getLevel(quiz)} ⭐</span></p>
      </div>
      <div className="mark-entry">
        <h4>Mid</h4>
        <p>{mid}/100 <span>{getLevel(mid)} ⭐</span></p>
      </div>
    </div>
  );

  return (
    <div className="marks-page">
      <h1>Your Marks</h1>
      <div className="marks-container">
        {marksData ? (
          Array.isArray(marksData) ? (
            marksData.map((item, idx) => <React.Fragment key={idx}>{renderMarkCard(item)}</React.Fragment>)
          ) : (
            renderMarkCard(marksData)
          )
        ) : (
          <p>Loading...</p>
        )}
      </div>
    </div>
  );
};

export default MarksShowing;
