// src/pages/student/AssignmentSubmission.js

// src/pages/student/AssignmentSubmission.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AssignmentSubmission.css';

function AssignmentSubmission() {
  const [assignment, setAssignment] = useState(null);
  const [submission, setSubmission] = useState(null);
  const [file, setFile] = useState(null);
  const [message, setMessage] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Fetch assignment details and submission status
  useEffect(() => {
    axios.get('http://localhost:5000/api/student/assignment')
      .then(res => {
        setAssignment(res.data.assignment);
        setSubmission(res.data.submission);
      })
      .catch(err => {
        console.error('Failed to load assignment:', err);
        setMessage('Error fetching assignment data');
      });
  }, []);

  // File handling
  const handleFileSelect = (e) => {
    setFile(e.target.files[0]);
    setMessage('');
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setFile(e.dataTransfer.files[0]);
    setMessage('');
  };

  const handleDragOver = (e) => e.preventDefault();

  // Calculate time remaining
  const formatTimeRemaining = () => {
    if (!assignment?.dueDate) return '';
    const now = new Date();
    const due = new Date(assignment.dueDate);
    const diff = due - now;

    if (diff <= 0) return 'Overdue';
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));
    const hours = Math.floor((diff / (1000 * 60 * 60)) % 24);
    const minutes = Math.floor((diff / (1000 * 60)) % 60);
    return `${days}d ${hours}h ${minutes}m`;
  };

  const handleSubmit = async () => {
    if (!file) return setMessage('Please select or drag a file first.');
    setIsSubmitting(true);

    try {
      const formData = new FormData();
      formData.append('assignmentFile', file);

      const url = submission
        ? 'http://localhost:5000/api/student/assignment/edit'
        : 'http://localhost:5000/api/student/assignment';

      const method = submission ? axios.put : axios.post;
      const res = await method(url, formData);

      setSubmission(res.data.submission);
      setMessage(res.data.message);
      setFile(null);
    } catch (err) {
      console.error('Submission error:', err);
      setMessage('Submission failed. Try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDelete = async () => {
    try {
      await axios.delete('http://localhost:5000/api/student/assignment');
      setSubmission(null);
      setMessage('Submission deleted.');
    } catch (err) {
      console.error('Delete error:', err);
      setMessage('Failed to delete submission.');
    }
  };

  return (
    <div className="assignment-container">
      <h2 className="course-title">Principles of Management</h2>

      <div className="assignment-header">
        <h4>Assignment 01</h4>
        <p><strong>Opened:</strong> {assignment?.openDate && new Date(assignment.openDate).toLocaleString()}</p>
        <p><strong>Due:</strong> {assignment?.dueDate && new Date(assignment.dueDate).toLocaleString()}</p>
      </div>

      <div className="assignment-status-box">
        <div className="status-row">
          <span>Submission Status:</span>
          <span>{submission ? 'Submitted' : 'No attempt'}</span>
        </div>
        <div className="status-row">
          <span>Time Remaining:</span>
          <span>{formatTimeRemaining()}</span>
        </div>
        <div className="status-row">
          <span>Last Modified:</span>
          <span>{submission?.submittedAt && new Date(submission.submittedAt).toLocaleString()}</span>
        </div>
        <div className="status-row">
          <span>File:</span>
          <span>{submission?.fileName || '-'}</span>
        </div>
      </div>

      <div
        className="dropzone"
        onDrop={handleDrop}
        onDragOver={handleDragOver}
      >
        <p>Drag & drop your assignment file here, or</p>
        <label className="file-select-label">
          Click to browse
          <input type="file" onChange={handleFileSelect} hidden />
        </label>
        {file && <p className="filename-preview">Selected: {file.name}</p>}
      </div>

      <div className="button-group">
        <button onClick={handleSubmit} disabled={isSubmitting} className="submit-btn">
          {submission ? 'Edit Submission' : 'Add Submission'}
        </button>
        {submission && (
          <button onClick={handleDelete} className="delete-btn">Delete Submission</button>
        )}
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
}

export default AssignmentSubmission;
