import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const SemesterDisplayPage = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const year = queryParams.get('year');

  // Placeholder data - replace with fetched data based on year
  const semesters = [1, 2]; // Assuming each year has 2 semesters

  return (
    <div className="instructor-page-container">
      <h1>Instructor Semester Display Page (Year {year})</h1>
      <h2>Select a Semester</h2>
      <ul>
        {semesters.map(semester => (
          <li key={semester}>
            <Link to={`/instructor/modules?year=${year}&semester=${semester}`}>Semester {semester}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SemesterDisplayPage;
