// src/pages/student/MarksShowing.js

import React, { useEffect, useState } from 'react';
import './MarksShowing.css';
import axios from 'axios';

const MarksShowing = () => {
  const [marks, setMarks] = useState(null);

  useEffect(() => {
    const fetchMarks = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/student/marks");
        setMarks(res.data);
      } catch (err) {
        console.error("Failed to load marks", err);
      }
    };
    fetchMarks();
  }, []);

  const getEvaluation = (score) => {
    if (score >= 75) return 'Excellent';
    if (score >= 60) return 'Very Good';
    return 'Good';
  };

  const getStar = (score, level) => {
    const evaluation = getEvaluation(score);
    return evaluation === level ? '⭐' : '';
  };

  if (!marks) {
    return <div className="marks-loading">Loading Marks...</div>;
  }

  return (
    <div className="marks-container">
      <h2 className="marks-title">Marks</h2>
      <div className="marks-grid">
        {/* Mid Marks */}
        <div className="mark-card">
          <h3>Mid Marks</h3>
          <div className="score-circle">{marks.midMarks}/100</div>
          <div className="evaluation">
            <div>Excellent {getStar(marks.midMarks, 'Excellent')}</div>
            <div>Very Good {getStar(marks.midMarks, 'Very Good')}</div>
            <div>Good {getStar(marks.midMarks, 'Good')}</div>
          </div>
        </div>

        {/* Quiz Marks */}
        <div className="mark-card">
          <h3>Quiz Marks</h3>
          <div className="score-circle">{marks.quizMarks}/100</div>
          <div className="evaluation">
            <div>Excellent {getStar(marks.quizMarks, 'Excellent')}</div>
            <div>Very Good {getStar(marks.quizMarks, 'Very Good')}</div>
            <div>Good {getStar(marks.quizMarks, 'Good')}</div>
          </div>
        </div>

        {/* Assignment Marks */}
        <div className="mark-card">
          <h3>Assignment Marks</h3>
          <div className="score-circle">{marks.assignmentMarks}/100</div>
          <div className="evaluation">
            <div>Excellent {getStar(marks.assignmentMarks, 'Excellent')}</div>
            <div>Very Good {getStar(marks.assignmentMarks, 'Very Good')}</div>
            <div>Good {getStar(marks.assignmentMarks, 'Good')}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MarksShowing;
