import React, { useState } from 'react';
import axios from 'axios';
import './InstructorNotificationForm.css';

const InstructorNotificationForm = ({ courses = [] }) => {
  const [formData, setFormData] = useState({
    title: '',
    message: '',
    course: '',
    isImportant: false
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setIsSubmitting(true);

    try {
      const response = await axios.post(
        'http://localhost:5000/api/instructor/notifications',
        formData,
        {
          headers: {
            'Content-Type': 'application/json',
            // Add authorization header if you have authentication
            // 'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (response.data.success) {
        setSuccess('Notification sent successfully!');
        setFormData({
          title: '',
          message: '',
          course: '',
          isImportant: false
        });
      } else {
        setError(response.data.error || 'Failed to send notification');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.error || 
                         err.message || 
                         'Server responded with error';
      setError(errorMessage);
      console.error('Submission error:', err);
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="notification-form-container">
      <h2>Create New Notification</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title *</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={(e) => setFormData({ ...formData, title: e.target.value })}
            required
            maxLength="100"
          />
        </div>

        <div className="form-group">
          <label>Message *</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={(e) => setFormData({ ...formData, message: e.target.value })}
            required
            rows="5"
            maxLength="500"
          />
        </div>

        {courses.length > 0 && (
          <div className="form-group">
            <label>Course (optional)</label>
            <select
              name="course"
              value={formData.course}
              onChange={(e) => setFormData({ ...formData, course: e.target.value })}
            >
              <option value="">Select a course</option>
              {courses.map((course) => (
                <option key={course._id} value={course._id}>
                  {course.name}
                </option>
              ))}
            </select>
          </div>
        )}

        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="isImportant"
            name="isImportant"
            checked={formData.isImportant}
            onChange={(e) => setFormData({ ...formData, isImportant: e.target.checked })}
          />
          <label htmlFor="isImportant">Mark as important</label>
        </div>

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}

        <button 
          type="submit" 
          className="submit-btn"
          disabled={isSubmitting}
        >
          {isSubmitting ? 'Sending...' : 'Send Notification'}
        </button>
      </form>
    </div>
  );
};

export default InstructorNotificationForm;
