import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import './InstructorNotificationForm.css';

const InstructorNotificationForm = () => {
  const [formData, setFormData] = useState({
    title: '',
    message: '',
    course: '',
    isImportant: false
  });
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchCourses = async () => {
      try {
        const { data } = await axios.get('/api/courses');
        setCourses(data.courses || data);
        setLoading(false);
      } catch (error) {
        toast.error('Failed to load courses');
        setLoading(false);
      }
    };
    fetchCourses();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const { data } = await axios.post('/api/instructor/notifications', formData);
      toast.success('Notification sent successfully!');
      setFormData({
        title: '',
        message: '',
        course: '',
        isImportant: false
      });
    } catch (error) {
      toast.error(error.response?.data?.error || 'Failed to send notification');
    }
  };

  if (loading) {
    return <div className="loading">Loading courses...</div>;
  }

  return (
    <div className="notification-form-container">
      <h2>Create New Notification</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Title *</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
            maxLength="100"
          />
        </div>

        <div className="form-group">
          <label>Message *</label>
          <textarea
            name="message"
            value={formData.message}
            onChange={handleChange}
            required
            rows="5"
            maxLength="500"
          />
        </div>

        <div className="form-group">
          <label>Course (optional)</label>
          <select
            name="course"
            value={formData.course}
            onChange={handleChange}
          >
            <option value="">Select a course</option>
            {courses.map(course => (
              <option key={course._id} value={course._id}>
                {course.name} ({course.code})
              </option>
            ))}
          </select>
        </div>

        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="isImportant"
            name="isImportant"
            checked={formData.isImportant}
            onChange={handleChange}
          />
          <label htmlFor="isImportant">Mark as important</label>
        </div>

        <button type="submit" className="submit-btn">
          Send Notification
        </button>
      </form>
    </div>
  );
};

export default InstructorNotificationForm;
