// StudentPrivateFiles.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css';

const StudentPrivateFiles = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadedFiles, setUploadedFiles] = useState([]);
  const [studentId] = useState('student123'); // static ID for now
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchUploadedFiles();
  }, []);

  const fetchUploadedFiles = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/student/files/${studentId}`);
      setUploadedFiles(response.data);
    } catch (error) {
      console.error('Error fetching files:', error);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setMessage("Please select a file.");
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);
    formData.append('studentId', studentId);

    try {
      await axios.post('http://localhost:5000/api/student/files/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      setMessage("File uploaded successfully.");
      setSelectedFile(null);
      fetchUploadedFiles();
    } catch (error) {
      console.error("Upload failed:", error);
      setMessage("Upload failed.");
    }
  };

  const handleDelete = async (fileId) => {
    try {
      await axios.delete(`http://localhost:5000/api/student/files/${fileId}`);
      fetchUploadedFiles();
    } catch (error) {
      console.error("Delete failed:", error);
    }
  };

  return (
    <div className="student-private-files">
      <h2>Upload Private Files</h2>

      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      {message && <p className="message">{message}</p>}

      <h3>Uploaded Files</h3>
      {uploadedFiles.length === 0 ? (
        <p>No files uploaded.</p>
      ) : (
        <ul>
          {uploadedFiles.map((file) => (
            <li key={file._id}>
              <a href={`http://localhost:5000/${file.filePath}`} target="_blank" rel="noreferrer">
                {file.originalName}
              </a>
              <button onClick={() => handleDelete(file._id)}>Delete</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StudentPrivateFiles;
