import React, { useState } from "react";
import "./AssignmentSubmission.css";
import { FaCheckCircle } from "react-icons/fa";

const AssignmentSubmission = () => {
  const [submitted, setSubmitted] = useState(false);
  const [fileName, setFileName] = useState("");
  const [editing, setEditing] = useState(false);

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFileName(file.name);
      setSubmitted(true);
      setEditing(false);
    }
  };

  const handleEdit = () => {
    setEditing(true);
    setSubmitted(false);
    setFileName("");
  };

  return (
    <div className="assignment-submission">
      <h2>Assignment Submission</h2>

      {!submitted || editing ? (
        <div className="upload-section">
          <input
            type="file"
            accept=".pdf,.doc,.docx"
            onChange={handleFileUpload}
          />
          <button className="mark-as-done-btn" onClick={() => setSubmitted(true)}>
            Mark as Done
          </button>
        </div>
      ) : (
        <div className="done-section">
          <button className="mark-as-done-btn done">
            <FaCheckCircle /> Done
          </button>
          <p>Uploaded File: {fileName}</p>
          <button className="edit-btn" onClick={handleEdit}>
            Edit Submission
          </button>
        </div>
      )}

      <table className="status-table">
        <thead>
          <tr>
            <th colSpan="2">Submission Status</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Submission Status</td>
            <td>{submitted ? "Submitted" : "No attempt"}</td>
          </tr>
          <tr>
            <td>Grading Status</td>
            <td>{submitted ? "Pending" : "Not Graded"}</td>
          </tr>
          <tr>
            <td>Time Remaining</td>
            <td>3 days and 5 hours</td>
          </tr>
          <tr>
            <td>Last Modified</td>
            <td>{submitted ? "Just now" : "-"}</td>
          </tr>
          <tr>
            <td>Submission Comments</td>
            <td>{submitted ? "You have submitted your file." : "Comments"}</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default AssignmentSubmission;
