import React, { useState, useEffect, useRef, useCallback } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css';

const StudentPrivateFiles = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [statusMessage, setStatusMessage] = useState({ text: '', type: '' });
  const [isDragging, setIsDragging] = useState(false);
  const fileInputRef = useRef();
  const dropAreaRef = useRef();

  const fetchFiles = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/files');
      if (data.success) {
        setFiles(data.files);
      } else {
        setStatusMessage({ text: data.message, type: 'error' });
      }
    } catch (err) {
      setStatusMessage({ 
        text: err.response?.data?.message || 'Failed to load files', 
        type: 'error' 
      });
    }
  };

  useEffect(() => { 
    fetchFiles(); 
  }, []);

  // Drag and drop handlers
  const handleDragEnter = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(true);
  }, []);

  const handleDragLeave = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
  }, []);

  const handleDragOver = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
  }, []);

  const handleDrop = useCallback((e) => {
    e.preventDefault();
    e.stopPropagation();
    setIsDragging(false);
    
    const droppedFiles = e.dataTransfer.files;
    if (droppedFiles.length > 0) {
      setSelectedFile(droppedFiles[0]);
      setStatusMessage({ text: '', type: '' });
    }
  }, []);

  useEffect(() => {
    const dropArea = dropAreaRef.current;
    if (dropArea) {
      dropArea.addEventListener('dragenter', handleDragEnter);
      dropArea.addEventListener('dragleave', handleDragLeave);
      dropArea.addEventListener('dragover', handleDragOver);
      dropArea.addEventListener('drop', handleDrop);

      return () => {
        dropArea.removeEventListener('dragenter', handleDragEnter);
        dropArea.removeEventListener('dragleave', handleDragLeave);
        dropArea.removeEventListener('dragover', handleDragOver);
        dropArea.removeEventListener('drop', handleDrop);
      };
    }
  }, [handleDragEnter, handleDragLeave, handleDragOver, handleDrop]);

  const handleFileChange = (e) => {
    if (e.target.files.length > 0) {
      setSelectedFile(e.target.files[0]);
      setStatusMessage({ text: '', type: '' });
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setStatusMessage({ text: 'Please select a file first', type: 'error' });
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      setUploadProgress(0);
      setStatusMessage({ text: 'Uploading...', type: 'info' });

      const { data } = await axios.post(
        'http://localhost:5000/api/files/upload', 
        formData,
        {
          headers: {
            'Content-Type': 'multipart/form-data'
          },
          onUploadProgress: (progress) => {
            const percent = Math.round((progress.loaded * 100) / progress.total);
            setUploadProgress(percent);
          }
        }
      );

      if (data.success) {
        setFiles(prevFiles => [data.file, ...prevFiles]);
        setStatusMessage({ 
          text: 'File uploaded successfully!', 
          type: 'success' 
        });
        fileInputRef.current.value = '';
        setSelectedFile(null);
      } else {
        setStatusMessage({ 
          text: data.message || 'Upload failed', 
          type: 'error' 
        });
      }
    } catch (err) {
      setStatusMessage({ 
        text: err.response?.data?.message || 'File upload failed', 
        type: 'error' 
      });
    } finally {
      setUploadProgress(0);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this file?')) return;
    
    try {
      const { data } = await axios.delete(`http://localhost:5000/api/files/${id}`);
      if (data.success) {
        setFiles(prevFiles => prevFiles.filter(file => file._id !== id));
        setStatusMessage({ 
          text: 'File deleted successfully', 
          type: 'success' 
        });
      } else {
        setStatusMessage({ 
          text: data.message || 'Delete failed', 
          type: 'error' 
        });
      }
    } catch (err) {
      setStatusMessage({ 
        text: err.response?.data?.message || 'Failed to delete file', 
        type: 'error' 
      });
    }
  };

  return (
    <div className="private-files-container">
      <h2 className="private-files-header">Private Files</h2>
      
      <div 
        ref={dropAreaRef}
        className={`upload-section ${isDragging ? 'dragging' : ''}`}
      >
        <div className="file-input-container">
          <div className="drop-area">
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileChange}
              accept="*"
              className="file-input"
              id="file-upload"
            />
            <label htmlFor="file-upload" className="drop-label">
              {selectedFile ? (
                <span className="file-selected">
                  Selected: {selectedFile.name}
                  <button 
                    className="clear-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      setSelectedFile(null);
                      fileInputRef.current.value = '';
                    }}
                  >
                    ×
                  </button>
                </span>
              ) : (
                <>
                  <span className="drop-text">
                    Drag & drop files here or click to browse
                  </span>
                  <span className="drop-hint">
                    (Supports all file types)
                  </span>
                </>
              )}
            </label>
          </div>
          <button 
            className="upload-btn"
            onClick={handleUpload}
            disabled={!selectedFile || uploadProgress > 0}
          >
            {uploadProgress > 0 ? `Uploading... ${uploadProgress}%` : 'Upload'}
          </button>
        </div>

        {uploadProgress > 0 && (
          <div className="progress-indicator">
            <div 
              className="progress-bar" 
              style={{ width: `${uploadProgress}%` }}
            ></div>
          </div>
        )}

        {statusMessage.text && (
          <div className={`status-message ${statusMessage.type}`}>
            {statusMessage.text}
          </div>
        )}
      </div>

      <div className="files-list">
        <h3 className="files-list-title">Your Files</h3>
        {files.length === 0 ? (
          <div className="empty-state">
            <p>No files uploaded yet</p>
          </div>
        ) : (
          <ul className="files-list-items">
            {files.map(file => (
              <li key={file._id} className="file-item">
                <div className="file-info">
                  <a 
                    href={`http://localhost:5000/uploads/${file.filename}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="file-link"
                  >
                    {file.originalname}
                  </a>
                  <span className="file-size">({Math.round(file.size / 1024)} KB)</span>
                  <span className="file-date">
                    {new Date(file.uploadedAt).toLocaleString()}
                  </span>
                </div>
                <button 
                  className="delete-btn"
                  onClick={() => handleDelete(file._id)}
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default StudentPrivateFiles;
