import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './StudentNotificationPage.css';

const StudentNotificationPage = () => {
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/student/notifications');
        setNotifications(res.data);
      } catch (error) {
        console.error('Error fetching notifications:', error);
      }
    };

    fetchNotifications();
  }, []);

  return (
    <div className="student-notification-page">
      <h2>Notifications</h2>
      {notifications.length === 0 ? (
        <p>No notifications available.</p>
      ) : (
        <ul>
          {notifications.map((notif) => (
            <li key={notif._id}>
              <h4>{notif.title}</h4>
              <p>{notif.message}</p>
              <small>By: {notif.createdBy} | {new Date(notif.createdAt).toLocaleString()}</small>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default StudentNotificationPage;
