import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css';

const StudentPrivateFiles = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [status, setStatus] = useState({ error: null, success: null });
  const fileInputRef = useRef();

  const fetchFiles = async () => {
    try {
      const { data } = await axios.get('http://localhost:5000/api/files');
      if (data.success) {
        setFiles(data.files);
        setStatus({ error: null, success: 'Files loaded successfully' });
      } else {
        setStatus({ error: data.message || 'Failed to load files', success: null });
      }
    } catch (err) {
      setStatus({ 
        error: err.response?.data?.message || 'Network error', 
        success: null 
      });
    }
  };

  useEffect(() => { fetchFiles(); }, []);

  const handleFileChange = (e) => {
    setSelectedFile(e.target.files[0]);
    setStatus({ error: null, success: null });
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setStatus({ error: 'Please select a file first', success: null });
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      setUploadProgress(0);
      setStatus({ error: null, success: null });

      const { data } = await axios.post(
        'http://localhost:5000/api/files/upload', 
        formData,
        {
          headers: { 'Content-Type': 'multipart/form-data' },
          onUploadProgress: (progress) => {
            setUploadProgress(Math.round((progress.loaded * 100) / progress.total));
          }
        }
      );

      if (data.success) {
        // Optimistically update the UI
        setFiles(prevFiles => [data.file, ...prevFiles]);
        setStatus({ error: null, success: data.message });
        fileInputRef.current.value = '';
        setSelectedFile(null);
      } else {
        setStatus({ error: data.message, success: null });
      }
    } catch (err) {
      setStatus({ 
        error: err.response?.data?.message || 'Upload failed', 
        success: null 
      });
    } finally {
      setUploadProgress(0);
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this file?')) return;
    
    try {
      await axios.delete(`http://localhost:5000/api/files/${id}`);
      // Optimistically update the UI
      setFiles(prevFiles => prevFiles.filter(file => file._id !== id));
    } catch (err) {
      setError('Failed to delete file');
      console.error('Delete error:', err);
      // Revert by refreshing from server
      fetchFiles();
    }
  };

  return (
    <div className="private-files-container">
      <h2 className="private-files-header">Private Files</h2>
      
      <div className="upload-section">
        <div className="file-input-container">
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileChange}
            accept="*"
            className="file-input"
          />
          <button 
            className="upload-btn"
            onClick={handleUpload}
            disabled={!selectedFile || uploadProgress > 0}
          >
            {uploadProgress > 0 ? `Uploading... ${uploadProgress}%` : 'Upload'}
          </button>
        </div>

        {uploadProgress > 0 && (
          <div className="progress-indicator">
            <div 
              className="progress-bar" 
              style={{ width: `${uploadProgress}%` }}
            ></div>
          </div>
        )}

        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
      </div>

      <div className="files-list">
        <h3 className="files-list-title">Your Files</h3>
        {files.length === 0 ? (
          <div className="empty-state">
            <p>No files uploaded yet</p>
          </div>
        ) : (
          <ul className="files-list-items">
            {files.map(file => (
              <li key={file._id} className="file-item">
                <a 
                  href={`http://localhost:5000/uploads/${file.filename}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="file-link"
                >
                  {file.originalname}
                </a>
                <span className="file-size">({Math.round(file.size / 1024)} KB)</span>
                <button 
                  className="delete-btn"
                  onClick={() => handleDelete(file._id)}
                >
                  Delete
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default StudentPrivateFiles;
