import React, { useState, useEffect, useCallback } from "react";
import axios from "axios";
import "./AssignmentSubmission.css";

const AssignmentSubmission = () => {
  const [assignment, setAssignment] = useState(null);
  const [file, setFile] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [message, setMessage] = useState("");

  const studentName = "John Doe"; // TODO: Replace with actual logged-in student data

  const fetchAssignmentStatus = useCallback(async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/student/assignment/${studentName}`);
      if (res.data) {
        setAssignment(res.data);
        setSubmitted(!!res.data.submittedAt);
        calculateTimeMessage(res.data);
      }
    } catch (err) {
      console.error("Error fetching assignment:", err);
    }
  }, [studentName]);

  useEffect(() => {
    fetchAssignmentStatus();
  }, [fetchAssignmentStatus]);

  const calculateTimeMessage = (data) => {
    if (!data.submittedAt || !data.dueDate) return;

    const submittedAt = new Date(data.submittedAt);
    const dueDate = new Date(data.dueDate);
    const diffMs = submittedAt - dueDate;

    const diffHours = Math.abs(diffMs) / 36e5;
    const days = Math.floor(diffHours / 24);
    const hours = Math.floor(diffHours % 24);
    const minutes = Math.floor((diffHours - Math.floor(diffHours)) * 60);

    if (diffMs < 0) {
      setMessage(`You submitted ${days} days, ${hours} hours and ${minutes} minutes early`);
    } else {
      setMessage(`Overdue by ${days} days, ${hours} hours and ${minutes} minutes`);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleSubmission = async () => {
    if (!file) return alert("Please select a file");

    const formData = new FormData();
    formData.append("studentName", studentName);
    formData.append("title", "Assignment 01");
    formData.append("file", file);

    try {
      const res = await axios.post("http://localhost:5000/api/student/assignment/submit", formData);
      setAssignment(res.data);
      setSubmitted(true);
      calculateTimeMessage(res.data);
      alert("Assignment submitted successfully");
    } catch (error) {
      console.error(error);
      alert("Submission failed");
    }
  };

  const handleEditSubmission = async () => {
    if (!file) return alert("Please select a file to update");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const res = await axios.put(`http://localhost:5000/api/student/assignment/edit/${studentName}`, formData);
      setAssignment(res.data);
      calculateTimeMessage(res.data);
      alert("Assignment updated successfully");
    } catch (error) {
      console.error(error);
      alert("Update failed");
    }
  };

  return (
    <div className="assignment-page">
      <div className="assignment-container">
        <h4 className="assignment-title">Principles of Management</h4>

        <div>
          <h5 className="assignment-subheading">Assignment 01</h5>
          <p className="assignment-dates">
            Opened: {assignment?.openDate ? new Date(assignment.openDate).toLocaleString() : "Loading..."}
          </p>
          <p className="assignment-dates">
            Due: {assignment?.dueDate ? new Date(assignment.dueDate).toLocaleString() : "Loading..."}
          </p>

          <table className="status-table">
            <thead>
              <tr>
                <th colSpan="2">Submission Status</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>Submission Status</td>
                <td>{submitted ? "Submitted" : "No attempt"}</td>
              </tr>
              <tr>
                <td>Grading Status</td>
                <td>{submitted ? "Pending Grading" : "Not Graded"}</td>
              </tr>
              <tr>
                <td>Time Remaining</td>
                <td>{message || "Calculating..."}</td>
              </tr>
              <tr>
                <td>Last Modified</td>
                <td>{assignment?.submittedAt ? new Date(assignment.submittedAt).toLocaleString() : "-"}</td>
              </tr>
              <tr>
                <td>Submission Comments</td>
                <td>{submitted ? "Submitted successfully." : "You have not made a submission yet."}</td>
              </tr>
            </tbody>
          </table>

          <div className="submission-actions">
            <input type="file" onChange={handleFileChange} />
            {!submitted ? (
              <button className="submit-btn" onClick={handleSubmission}>Add Submission</button>
            ) : (
              <button className="submit-btn" onClick={handleEditSubmission}>Edit Submission</button>
            )}
            <button className="back-btn">Back To Course</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssignmentSubmission;
