import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StudentPrivateFiles.css';

const StudentPrivateFiles = () => {
  const [file, setFile] = useState(null);
  const [files, setFiles] = useState([]);

  useEffect(() => {
    fetchFiles();
  }, []);

  const fetchFiles = async () => {
    const res = await axios.get('http://localhost:5000/api/student/files');
    setFiles(res.data);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    if (!file) return;

    const formData = new FormData();
    formData.append('file', file);

    await axios.post('http://localhost:5000/api/student/files/upload', formData);
    setFile(null);
    fetchFiles();
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://localhost:5000/api/student/files/${id}`);
    fetchFiles();
  };

  return (
    <div className="private-files-container">
      <h2>My Private Files</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>

      <div className="file-list">
        {files.map((f) => (
          <div key={f._id} className="file-card">
            {f.mimetype.startsWith('image/') ? (
              <img src={`http://localhost:5000/${f.path}`} alt={f.originalname} />
            ) : (
              <a href={`http://localhost:5000/${f.path}`} target="_blank" rel="noreferrer">
                {f.originalname}
              </a>
            )}
            <button onClick={() => handleDelete(f._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StudentPrivateFiles;
