import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StudentNotificationPage.css';

const StudentNotificationPage = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchNotifications = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/student/notifications');
      if (response.data.success) {
        setNotifications(response.data.notifications);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 5000); // Refresh every 5 seconds
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="notifications-container">
      <h2>Notifications</h2>
      {notifications.map(notification => (
        <div key={notification._id} className="notification-card">
          <h3>{notification.title}</h3>
          <p>{notification.message}</p>
          <small>From: {notification.instructor?.name}</small>
        </div>
      ))}
    </div>
  );
};

export default StudentNotificationPage;
