import React, { useEffect, useState } from "react";
import axios from "axios";
import "./AssignmentSubmission.css";

const AssignmentSubmission = () => {
  const openDate = new Date("2025-04-05T00:00:00");
  const dueDate = new Date("2025-05-12T00:00:00");
  const studentId = "12345"; // Replace with dynamic ID if using auth

  const [file, setFile] = useState(null);
  const [submittedAt, setSubmittedAt] = useState(null);
  const [submissionData, setSubmissionData] = useState(null);
  const [dragOver, setDragOver] = useState(false);

  const API_BASE = "http://localhost:5000/api/student/assignment";

  useEffect(() => {
    fetchSubmission();
  }, []);

  const fetchSubmission = async () => {
    try {
      const res = await axios.get(`${API_BASE}/${studentId}`);
      setSubmissionData(res.data);
      setSubmittedAt(new Date(res.data.submittedAt));
    } catch (err) {
      console.log("No submission yet.");
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    if (e.dataTransfer.files.length > 0) {
      setFile(e.dataTransfer.files[0]);
    }
  };

  const handleSubmit = async () => {
    if (!file) return;
    const formData = new FormData();
    formData.append("studentId", studentId);
    formData.append("assignmentTitle", "Assignment 01");
    formData.append("subject", "Principles of Management");
    formData.append("file", file);

    try {
      const res = await axios.post(API_BASE, formData);
      setSubmissionData(res.data);
      setSubmittedAt(new Date(res.data.submittedAt));
      setFile(null);
    } catch (err) {
      alert("Submission failed.");
    }
  };

  const handleEdit = () => {
    setFile(null);
    setSubmittedAt(null);
    axios.delete(`${API_BASE}/${submissionData._id}`).then(() => {
      setSubmissionData(null);
    });
  };

  const getTimeRemainingText = () => {
    const now = submittedAt || new Date();
    const diffMs = dueDate - now;
    const absDiff = Math.abs(diffMs);
    const diffDays = Math.floor(absDiff / (1000 * 60 * 60 * 24));
    const diffHours = Math.floor((absDiff / (1000 * 60 * 60)) % 24);

    if (!submittedAt) {
      return `${diffMs >= 0 ? "" : "Overdue by "}${diffDays} days ${diffHours} hours`;
    }
    return submittedAt < dueDate
      ? `Submitted ${diffDays} days ${diffHours} hours early`
      : `Overdue by ${diffDays} days ${diffHours} hours`;
  };

  return (
    <div className="assignment-page">
      <div className="assignment-container">
        <h4 className="assignment-title">Principles of Management</h4>
        <h5 className="assignment-subheading">Assignment 01</h5>
        <p className="assignment-dates">Opened: {openDate.toLocaleString()}</p>
        <p className="assignment-dates">Due: {dueDate.toLocaleString()}</p>

        <table className="status-table">
          <thead>
            <tr><th colSpan="2">Submission Status</th></tr>
          </thead>
          <tbody>
            <tr>
              <td>Submission Status</td>
              <td>{submissionData ? "Submitted" : "No attempt"}</td>
            </tr>
            <tr>
              <td>Grading Status</td>
              <td>{submissionData ? "Not Graded" : "-"}</td>
            </tr>
            <tr>
              <td>Time Remaining</td>
              <td>{getTimeRemainingText()}</td>
            </tr>
            <tr>
              <td>Last Modified</td>
              <td>{submittedAt ? submittedAt.toLocaleString() : "-"}</td>
            </tr>
            <tr>
              <td>Submission Comments</td>
              <td>{submissionData ? "Submitted successfully." : "No comments"}</td>
            </tr>
          </tbody>
        </table>

        <div className="submission-actions">
          {!submissionData ? (
            <>
              <div
                className={`drop-zone ${dragOver ? "drag-over" : ""}`}
                onDragOver={(e) => { e.preventDefault(); setDragOver(true); }}
                onDragLeave={() => setDragOver(false)}
                onDrop={handleDrop}
              >
                <p>Drag and drop your file here</p>
              </div>
              <input
                type="file"
                onChange={handleFileChange}
                accept=".pdf,.doc,.docx"
                className="file-input"
              />
              <button className="submit-btn" onClick={handleSubmit} disabled={!file}>
                Submit Assignment
              </button>
            </>
          ) : (
            <>
              <p className="uploaded-file">Uploaded: {submissionData.fileName}</p>
              <button className="edit-btn" onClick={handleEdit}>Edit Submission</button>
            </>
          )}
          <button className="back-btn">Back To Course</button>
        </div>
      </div>
    </div>
  );
};

export default AssignmentSubmission;
