import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './StudentNotificationPage.css';

const StudentNotificationPage = () => {
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchNotifications = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/student/notifications', {
        headers: {
          // Add authorization header if you have authentication
          // 'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (response.data.success) {
        setNotifications(response.data.notifications);
      } else {
        setError(response.data.error || 'Failed to load notifications');
      }
    } catch (error) {
      setError(error.response?.data?.error || error.message || 'Error fetching notifications');
      console.error('Fetch error:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchNotifications();
    const interval = setInterval(fetchNotifications, 30000); // Refresh every 30 seconds
    
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return <div className="notifications-container">Loading notifications...</div>;
  }

  if (error) {
    return <div className="notifications-container error">{error}</div>;
  }

  return (
    <div className="notifications-container">
      <h2>Notifications</h2>
      {notifications.length === 0 ? (
        <div className="empty-state">No notifications available</div>
      ) : (
        <div className="notifications-list">
          {notifications.map((notification) => (
            <div 
              key={notification._id} 
              className={`notification-card ${notification.isImportant ? 'important' : ''}`}
            >
              <h3>{notification.title}</h3>
              <p>{notification.message}</p>
              <div className="notification-footer">
                <small>
                  {notification.course?.name && `Course: ${notification.course.name} • `}
                  {new Date(notification.createdAt).toLocaleString()}
                </small>
                {notification.instructor?.name && (
                  <small>Instructor: {notification.instructor.name}</small>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default StudentNotificationPage;
